00063         private void InitializeComponents(string ruleFileName)
00064         {
00065             // <exception cref="ArgumentNullException">Thrown when <paramref name="ruleFileName"/> is null.</exception>
00066             if (ruleFileName == null)
00067             {
00068                 throw new ArgumentNullException(nameof(ruleFileName));
00069             }
00070 
00071             _ruleFile = new RuleFile();
00072             _lib = new Lib();
00073             _database = new DataBase();
00074 
00075             // Deserialize rule file
00076             _ruleFile.Deserialize(ruleFileName);
00077 
00078             // Initialize log file
00079             _lib.ResetLog(_ruleFile.rule.logFileName);
00080 
00081             // Initialize database
00082             _dataBaseFileName = Path.GetFullPath(_ruleFile.rule.DataBasePath + "\\" + _ruleFile.rule.DataBaseFileName);
00083             _lib.WriteMessage($"DataBase path: {_dataBaseFileName}", _silent);
00084             _database.InitializeDataBase(_dataBaseFileName);
00085         }
