00087         public void CollectFileData(string ruleFileName, string inputDirectory, string outputDirectory, bool silent)
00088         {
00089             ExifTool exifTool = new SharpExifTool.ExifTool();
00090             FileMetaInfo fileMetaInfo = new FileMetaInfo();
00091             List<string> NoMetadataRule = new List<string>();
00092             PropertyInfo propertyInfo;
00093             DateTime currentDateTimeValue = DateTime.MinValue;
00094 
00095             var value = "";
00096             string lastField = "";
00097 
00098             _silent = silent;
00099 
00100             InitializeComponents(ruleFileName);
00101 
00102             string[] directories = Directory.GetFiles(inputDirectory, "*", SearchOption.AllDirectories);
00103             Array.Sort(directories);
00104 
00105             _lib.WriteMessage($"Collecting file data from {inputDirectory} with {directories.Length} files.", silent);
00106 
00107             _fileCount = 1;
00108 
00109             _lib.WriteMessage("\n", silent);
00110 
00111             AnsiConsole.Progress()
00112                 .AutoClear(false)
00113 
00114                 .Columns(new ProgressColumn[]
00115                     {
00116                         new TaskDescriptionColumn(),    // Task description
00117                         new ProgressBarColumn(),        // Progress bar
00118                         new PercentageColumn(),         // Percentage
00119                         new SpinnerColumn(),            // Spinner
00120                     })
00121 
00122                 .Start(ctx =>
00123                 {
00124                     var task = ctx.AddTask("[green]Processing files...[/]");
00125                     task.MaxValue(directories.Length);
00126 
00127                     foreach (string dirString in directories)
00128                     {
00129                         //_lib.WriteMessage($"File {dirString} processed.", silent);
00130                         task.Increment(1);
00131 
00132                         if (dirString != null && dirString.Length == 0 && dirString != string.Empty && dirString != "")
00133                         {
00134                             _lib.WriteMessage("Skipping empty file path.", "red", silent);
00135                             continue;
00136                         }
00137 
00138                         if (!File.Exists(dirString))
00139                         {
00140                             _lib.WriteMessage($"File does not exist: {dirString}", "red", silent);
00141                             continue;
00142                         }
00143 
00144                         FileInfo fileInfo = new FileInfo(dirString);
00145                         ICollection<KeyValuePair<string, string>> exifData = exifTool.ExtractAllMetadata(dirString);
00146                         Dictionary<string, string> exifDataDictionary = exifData.ToDictionary();
00147 
00148                         if (!_ruleFile.rule.metaInfoField.ContainsKey(fileInfo.Extension.ToUpperInvariant()))
00149                         {
00150                             if (!NoMetadataRule.Contains(fileInfo.Extension.ToUpperInvariant()))
00151                                 NoMetadataRule.Add(fileInfo.Extension.ToUpperInvariant());
00152                             continue;
00153                         }
00154 
00155                         MetaInfoField metaInfoField = _ruleFile.rule.metaInfoField[fileInfo.Extension.ToUpperInvariant()];
00156 
00157                         // All schlüsselwürter für diesen Dateityp durchgehen und
00158                         // die werte holen und in die datenbank schreiben
00159                         foreach (var field in metaInfoField.availableKeyWords)
00160                         {
00161                             if (lastField != field.Key)
00162                             {
00163                                 lastField = field.Key;
00164                                 currentDateTimeValue = fileInfo.CreationTime; // Reset currentDateTimeValue for each new field
00165                             }
00166 
00167 
00168                             foreach (var keyword in field.Value.KeyWords)
00169                             {
00170                                 if (field.Value.Source == "EXIFTOOL")
00171                                 {
00172                                     if (!exifDataDictionary.TryGetValue(keyword, out value))
00173                                     {
00174                                         //_lib.WriteMessage($"Keyword '{keyword}' not found in EXIF data for file {fileInfo.Name}.", silent);
00175                                         continue;
00176                                     }
00177                                 }
00178                                 else if (field.Value.Source == "FILEINFO")
00179                                 {
00180                                     if (!fileInfo.GetType().GetProperty(keyword)?.CanRead ?? true)
00181                                     {
00182                                         //_lib.WriteMessage($"Keyword '{keyword}' not found in FileInfo for file {fileInfo.Name}.", silent);
00183                                         continue;
00184                                     }
00185                                     value = fileInfo.GetType().GetProperty(keyword)?.GetValue(fileInfo)?.ToString() ?? string.Empty;
00186                                 }
00187                                 else
00188                                 {
00189                                     //_lib.WriteMessage($"Unknown source '{field.Value.Source}' for keyword '{keyword}'.", silent);
00190                                     continue;
00191                                 }
00192 
00193 
00194                                 switch (field.Value.DataType.ToUpperInvariant())
00195                                 {
00196                                     case "STRING":
00197                                         propertyInfo = fileMetaInfo.GetType().GetProperty(field.Key);
00198                                         propertyInfo.SetValue(fileMetaInfo, value, null);
00199                                         break;
00200 
00201                                     case "INT":
00202                                         if (int.TryParse(value, out int intValue))
00203                                         {
00204                                             propertyInfo = fileMetaInfo.GetType().GetProperty(field.Key);
00205                                             propertyInfo.SetValue(fileMetaInfo, intValue, null);
00206                                         }
00207                                         break;
00208 
00209                                     case "LONG":
00210                                         if (long.TryParse(value, out long longValue))
00211                                         {
00212                                             propertyInfo = fileMetaInfo.GetType().GetProperty(field.Key);
00213                                             propertyInfo.SetValue(fileMetaInfo, longValue, null);
00214                                         }
00215                                         break;
00216 
00217                                     case "DOUBLE":
00218                                         if (double.TryParse(value, out double doubleValue))
00219                                         {
00220                                             propertyInfo = fileMetaInfo.GetType().GetProperty(field.Key);
00221                                             propertyInfo.SetValue(fileMetaInfo, doubleValue, null);
00222                                         }
00223                                         break;
00224 
00225                                     case "BOOLEAN":
00226                                         if (bool.TryParse(value, out bool boolValue))
00227                                         {
00228                                             propertyInfo = fileMetaInfo.GetType().GetProperty(field.Key);
00229                                             propertyInfo.SetValue(fileMetaInfo, boolValue, null);
00230                                         }
00231                                         break;
00232 
00233                                     case "DATETIME":
00234                                         CultureInfo enUS = new CultureInfo("en-US");
00235                                         DateTime dateTimeValue;
00236 
00237                                         if (
00238                                             (DateTime.TryParseExact(value, "dd.MM.yyyy HH:mm:ss", new CultureInfo("de-DE"), DateTimeStyles.None, out dateTimeValue)) ||
00239                                             (DateTime.TryParseExact(value, "yyyy:MM:dd HH:mm:ss", null, DateTimeStyles.None, out dateTimeValue)) ||
00240                                             (DateTime.TryParseExact(value, "yyyy:MM:dd HH:mm:sszzz", null, DateTimeStyles.None, out dateTimeValue)) ||
00241                                             (DateTime.TryParseExact(value, "yyyy:MM:dd", null, DateTimeStyles.None, out dateTimeValue))
00242                                             )
00243                                         {
00244                                             if (field.Value.ValAttribute == "LOWEST" && dateTimeValue < currentDateTimeValue)
00245                                                 currentDateTimeValue = dateTimeValue;
00246                                             else if (field.Value.ValAttribute == "HIGHEST" && dateTimeValue > currentDateTimeValue)
00247                                                 currentDateTimeValue = dateTimeValue;
00248                                             else
00249                                                 currentDateTimeValue = dateTimeValue;
00250 
00251                                             propertyInfo = fileMetaInfo.GetType().GetProperty(field.Key);
00252                                             propertyInfo.SetValue(fileMetaInfo, currentDateTimeValue, null);
00253                                         }
00254                                         break;
00255 
00256                                     default:
00257                                         _lib.WriteMessage($"Unknown data type: {field.Value.DataType}", "red", silent);
00258                                         break;
00259                                 }
00260 
00261                             }
00262                         }
00263 
00264                         PaternInterpreter paternInterpreter = new PaternInterpreter();
00265 
00266                         // Die Regeln für diesen Dateityp holen
00267                         FileRules fileRules = _ruleFile.rule.fileRules[fileInfo.Extension.ToUpperInvariant()];
00268 
00269                         string sortingPath = paternInterpreter.PaternParser(paternInterpreter.PaternLexer(fileRules.SortingPatern), fileMetaInfo);
00270                         string newFileName = paternInterpreter.PaternParser(paternInterpreter.PaternLexer(fileRules.FileNamePatern), fileMetaInfo);
00271 
00272                         if (fileRules.RootPath != null && fileRules.RootPath != string.Empty)
00273                             if (fileRules.RootPath == "{MediaRootPath}")
00274                                 fileRules.RootPath = _ruleFile.rule.MediaRootPath;
00275 
00276                         if (fileRules.RootPath.EndsWith("\\") || fileRules.RootPath.EndsWith("/"))
00277                             fileMetaInfo.NewFilePath = Path.GetFullPath(fileRules.RootPath + sortingPath + "\\" + newFileName);
00278                         else
00279                             fileMetaInfo.NewFilePath = Path.GetFullPath(fileRules.RootPath + "\\" + sortingPath + "\\" + newFileName);
00280 
00281                         fileMetaInfo.Id = _fileCount;
00282                         _database.AddFileMetaInfo(fileMetaInfo, _dataBaseFileName);
00283                         _lib.WriteMessage($"{_fileCount} - File {fileMetaInfo.FileName} processed. Size: {fileMetaInfo.FileSize}", "aqua", silent);
00284                         _fileCount++;
00285                     }
00286                 });
00287 
00288             _lib.WriteMessage("\n======================================", "gold3", false);
00289             _lib.WriteMessage($"{_fileCount - 1} files processed.", false);
00290             _lib.WriteMessage($"\nNo metadata rules defined for ", silent);
00291             foreach (var ext in NoMetadataRule)
00292             {
00293                 _lib.WriteMessage($" {ext} ", "yellow", silent);
00294             }
00295         }
